
$(function(){loadSpecialStylesheets();newWindow();programNotes();thumbnails();photoLoader();keys();});function loadSpecialStylesheets(){if(jQuery.browser.safari){$('head').append("<link type='text/css' rel='stylesheet' href='/style/safari.css'>");}
if(jQuery.browser.chrome){$('head').append("<link type='text/css' rel='stylesheet' href='/style/chrome.css'>");}
if(jQuery.browser.opera){$('head').append("<link type='text/css' rel='stylesheet' href='/style/opera.css'>");}}
function newWindow(){$('a.new-window').click(function(){window.open(this.href);return false;});}
function programNotes(){$('.programNotesOpen').click(function(){$(this).prevAll('.programNotes').slideDown('medium');$(this).html('').animate({marginBottom:"0px"},'medium');});$('.programNotesClose').click(function(){$(this).parent().slideUp('medium');$(this).parent().nextAll('.programNotesOpen').html('[+]&nbsp;Program Notes').animate({marginBottom:"30px"},'medium');});}
function thumbnails(){offOp=0.4;$('#galleryThumbs').find('img:not(img.on)').mouseover(function(){$(this).animate({opacity:1},200);}).mouseout(function(){$(this).animate({opacity:offOp},200);});}
function photoLoader(){if($('#photoLoaderInfo').length!==0){siteSection=$('#photoLoaderSiteSection').html();section=$('#photoLoaderSection').html();id=$('#photoLoaderID').html();width=$('#photoLoaderWidth').html();height=$('#photoLoaderHeight').html();title=$('#photoLoaderTitle').html();if(siteSection=='photography'){srcString='/media/'+siteSection+'/'+section+'/'+id+'.jpg';}else{srcString='/media/'+siteSection+'/'+id+'.jpg';}
$(function(){var img=new Image();$(img).load(function(){$(this).hide();$('#pictureDiv').removeClass('loading').append(this);$(this).fadeIn();}).attr('src',srcString).attr('width',width).attr('height',height).attr('alt',title);});}}
function keys(){if($('a#previous').length!==0){$(document).bind('keydown','left',function(){location=$('a#previous').attr('href');});}
if($('a#next').length!==0){$(document).bind('keydown','right',function(){location=$('a#next').attr('href');});}}